function [cdf_emp,x] = empcdf(data,nx);%function [cdf_emp,x] = empcdf(data,nx);%INPUT       data    measured data%            nx      length of x%                    OPTIONAL, default nx=1000%OUTPUT      cdf_emp empirical distribution function%            x       points where cdf computed if (nargin==1)  nx = 1000;%length(data); endm = min(data); M = max(data);x = linspace(m,M,nx);ndata = length(data);  for i = 1:nx  cdf_emp(i) = sum(data<=x(i)); endcdf_emp = cdf_emp/ndata;